#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <time.h>
int d(const char*a,const char*b){int la=strlen(a),lb=strlen(b),i,j,D[99]={la};for(i=1;i<=la;i++)D[i]=i;for(i=1;i<=lb;i++)for(j=la,D[0]=i;j;j--)D[j]=a[--j]==b[i-1]?D[j-1]:1+(D[j]<D[j-1]?D[j]:D[j-1]);return D[la];}int main(){char u[99];srand(time(0));printf("Stupid Chatbot: Hello! What can I do for you today?\n");while(1){printf("You: ");fgets(u,99,stdin);u[strlen(u)-1]='\0';const char*r[]={"Hi there!","Hello!","Greetings!","Hey!","I'm just a computer program, so I don't have feelings.","I'm here to assist you, so I'm doing well!","I'm as good as the code that powers me!","I don't have a name. You can call me Chatbot!","I go by Chatbot. How can I help you today?","I'm just Chatbot, here to assist you!"};int m=99,i=sizeof(r)/sizeof(r[0]);while(i--){int x=d(u,r[i]);if(x<m)m=x,printf("Stupid Chatbot: %s\n",r[i]);}if(m>5){const char*x[]={"Sorry, I don't understand. Can you please rephrase?","I'm not sure what you mean. Could you try again?","Hmmm, that's a bit confusing. Can you provide more details?","I'm just a simple chatbot, and I didn't get that. Can you reword your question?"};printf("Stupid Chatbot: %s\n",x[rand()%4]);}}return 0;}
